import React, { useEffect, useState } from "react";
import MoviesCard from '../MoviesCard/MoviesCard';
import './MoviesCardList.css';

function MoviesList({ movieSearchResult, handleSetLike, handleRemoveLike, isLoggedIn }) {

  const searchresults = movieSearchResult.length > 0 ?
    JSON.parse(localStorage.getItem('searchResults')) :
    [];

  return (
    <div className="movies-list">
      <div className="movies-list__container">
        {
          searchresults.length > 0 &&
          searchresults.map((movie) => {
            return <MoviesCard
              key={movie.movieId || movie._id}
              movie={movie}
              handleSetLike={handleSetLike}
              handleRemoveLike={handleRemoveLike} />
          })
        }
      </div>

      <button 
        className="movies-list__button-more">
        Ещё
      </button>
    </div>

  )
}

export default MoviesList;
