import React from "react";
import Header from '../Header/Header';
import SearchForm from '../SearchForm/SearchForm';
import MoviesCardList from '../MoviesCardList/MoviesCardList';
import Footer from '../Footer/Footer';
import './Movies.css';

function Movies({ movies, movieSearchResult, searchWord, handleSetLike, handleRemoveLike, onSearch, isLoggedIn }) {

  // Запоминаем результаты поиска; 
  // если результатов нет - значит поиск еще не осуществлялся
  // и элемента с фильмами быть не должно

  // Если пользователь повторно переходит на страницу фильмов,
  // то при монтировании компонента данные достаются
  // из локального хранилища. 

  console.log('In Movies: I rendered again!')

  return (
    <div className="movies">
      <Header isLoggedIn={true} />
      <SearchForm
        searchWord={searchWord}
        movies={movies}
        onSearch={onSearch} />
        <MoviesCardList
          movieSearchResult={movieSearchResult}
          handleSetLike={handleSetLike}
          onDislike={handleRemoveLike}
          isLoggedIn={isLoggedIn} />
      <Footer />
    </div>
  )
}

export default Movies;
