import React, { useEffect, useState } from "react";
import { Route, Switch, useHistory, useLocation } from 'react-router-dom';
import Main from '../Main/Main';
import Movies from '../Movies/Movies';
import SavedMovies from '../SavedMovies/SavedMovies';
import Profile from '../Profile/Profile';
import Login from '../Login/Login';
import Register from '../Register/Register';
import Page404 from '../Page404/Page404';
import InfoTooltip from '../InfoTooltip/InfoTooltip';
import ProtectedRoute from '../ProtectedRoute/ProtectedRoute';
import * as tooltip from '../../utils/tooltip';
import * as MainApi from '../../utils/MainApi';
import * as MoviesApi from '../../utils/MoviesApi';
import './App.css';
import { CurrentUserContext } from '../../contexts/CurrentUserContext';

function App() {

  const history = useHistory();
  const location = useLocation();

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState({});
  const [isInfoToolTipOpen, setIsInfoToolTipOpen] = useState(false);
  const [infoToolTipInfo, setInfoToolTipInfo] = useState({});
  const [movies, setMovies] = useState([]); // Фильмы, полученные от Api
  const [savedMovies, setSavedMovies] = useState([]); // Фильмы, сохраненные пользователем
  const [isLoading, setIsLoading] = useState(false);

  // ЛОКАЛЬНОЕ ХРАНИЛИЩЕ

  // Фильмы, полученные после фильтрации
  const searchResults = JSON.parse(localStorage.getItem('searchResults'));
  // Текст запроса
  const searchWord = JSON.parse(localStorage.getItem('searchWord'));
  // Состояние переключателя
  const filterState = localStorage.getItem('filterState');

  // Обработка регистрации

  function handleRegister (name, email, password) {
    MainApi.register(email, password, name)
      .then((res) => {
        if (res.ok) {
          history.push('/signin');
          setInfoToolTipInfo({
            mesage: tooltip.registrationSuccess,
            image: tooltip.successIcon
          });
        } else {
          setInfoToolTipInfo({
            message: tooltip.registrationFail,
            image: tooltip.successIcon
          })
        }
      })
      .catch((err) => {
        console.log(err);
        setInfoToolTipInfo({
          message: tooltip.registrationFail,
          image: tooltip.failIcon
        });
      })
      .finally(() => {
        setIsInfoToolTipOpen(true);
      })
  }

  // Обработка логина

  function handleLogin (email, password) {
    if (!email || !password) {
      console.log('No email or password on login; aborting.');
      return;
    }

    MainApi.login(email, password)
      .then((data) => {
        console.log(`In handleLogin: res data: ${data.jwt}`);
        if(!data.jwt) {
          throw new Error('Произошла ошибка (авторизации на фронте)');
        }
        localStorage.setItem('jwt', data.jwt);
        return data._id;
      })
      .then((id) => {
        tokenCheck();
        setCurrentUser({
          _id: id,
        });
        setIsLoggedIn(true);
        history.push('/movies');
        setInfoToolTipInfo({
          message: tooltip.loginSuccess,
          image: tooltip.successIcon
        })
      })
      .catch((err) => {
        console.log(err);
        setInfoToolTipInfo({
          message: tooltip.loginFail,
          image: tooltip.failIcon
        });
      })
      .finally(() => {
        console.log(`Before Popup open: ${isLoggedIn}`)
        setIsInfoToolTipOpen(true);
      });
  }

  // Обработка обновления профиля

  function handleEditProfileSubmit (name, email) {
    MainApi.editProfile(name, email)
      .then((data) => {
        setCurrentUser(data);
        setInfoToolTipInfo({
          message: tooltip.updateSuccess,
          image: tooltip.successIcon
        })
      })
      .catch((err) => {
        console.log(err);
        setInfoToolTipInfo({
          message: tooltip.updateFail,
          image: tooltip.failIcon
        })
      })
      .finally(() => {
        setIsInfoToolTipOpen(true)
      });
  }

  function handleLogOut () {
    localStorage.removeItem('jwt');
    setIsLoggedIn(false);
    history.push('/signin');
  }

  function closePopups () {
    setIsInfoToolTipOpen(false);
  }

  // Добавить фильм в библиотеку сохраненных фильмов
  // или оповестить юзера об ошибке

  function handleSetLike (movie) {
    MainApi.createLocalCard(movie)
      .then((createdMovie) => {
        console.log(`Saved movie in handleSetLike: ${createdMovie}`);
        setSavedMovies([createdMovie, ...savedMovies])
      })
      .catch((err) => {
        console.log(err);
        setInfoToolTipInfo({
          message: tooltip.saveFail,
          image: tooltip.failIcon
        });
        setIsInfoToolTipOpen(true);
      })
  }

  function handleRemoveLike (movie) {
    MainApi.deleteLocalMovie(movie.movieId)
      .then(() => {
        MainApi.getSavedMovies
          .then((movies) => {
            setSavedMovies(movies);
          })
          .catch((err) => {
            console.log(err);
          })
      })
      .catch((err) => {
        console.log(err);
        setInfoToolTipInfo({
          message: tooltip.removeFail,
          image: tooltip.failIcon
        });
        setIsInfoToolTipOpen(true);
      })
  }

  // Получить все сохраненные фильмы с сервера

  function getSavedMovies() {
    console.log(`Login State in getSavedMovies on login:${isLoggedIn}`);
    if(isLoggedIn) {
      MainApi.getSavedMovies()
      .then((data) => {
        console.log(`MainApi.getSavedMovies result: ${JSON.stringify(data)}`);
        if(data) {
          setSavedMovies(JSON.stringify(data));
          console.log(`Setting saved movies: ${savedMovies}`);
          return
        }
        setSavedMovies([]);
      })
      .catch((err) => {
        console.log(err);
      })
    }
  }

  // Получить фильмы с API
  // происходит при авторизации

  function getMovies() {
    MoviesApi.getMovies()
    .then((data) => {
      if(data) {
        console.log(`MoviesApi response: ${JSON.stringify(data)}`);
        setMovies(JSON.stringify(data));
        return;
      }
      setMovies([]);
    })
    .catch((err) => {
      console.log(err);
    })
  }

  // После отправки поскового запроса 
  // сначала отображаем прелодер, затем
  // забираем все фильмы с api и сохраняем в переменную состояния.
  // После сохранения фильмов фильтруем их по поисковому слову

  function handleSearchRequest(value) {
    setIsLoading(true);
    assembleMoviesList(value)
      .then(() => {
        setIsLoading(false);
      });
  }

  // Функция по фильтрации массивов с фильмами по ключевому слову

  function filterBySearchWord(movies, word) {
    console.log(`In filterBySearchWord: movies: ${movies}`);
    movies.filter((movie) => {
      return movie.nameRU.toLowerCase().includes(word.toLowerCase());
    })
  }

  // При отображении фильмов в блоке результатов
  // фильмы, ранее сохраненные пользователем берутся с сервера
  // (так как только на них будет ярлык сохраненных));

  function assembleMoviesList(word) {
    // Проверяем все фильмы
    // Проверяем сохраненные фильмы
    const allFilteredMovies = filterBySearchWord(movies, word);
    const allSavedMovies = filterBySearchWord(savedMovies, word);

    // Сравниваем два массива,
    // повторяющиеся фильмы берем с серверной стороны
    const movieList = allFilteredMovies.map((movie) => {
      const savedMovie = allSavedMovies.find((savedM) => savedM.movieId === movie.id);
      return savedMovie ?? movie;
    })

    localStorage.setItem('searchResults', movieList);
    console.log(`assembleMoviesList: ${localStorage.getItem('searchResults')}`);
  }

  // Проверяем наличие токена в локальном хранилище
  // если токен присутствует - 
  // меняем состояние логина и
  // забираем данные о юзере с сервера и сохранем в переменную состояния. 

  function tokenCheck () {
    const jwt = localStorage.getItem('jwt');

    console.log(`Token in tokenCheck: ${jwt}`);

    if (jwt) {
      MainApi.getContent(jwt)
        .then((user) => {
          if (user) {
            setIsLoggedIn(true);
            setCurrentUser(user);
            location.pathname === '/signin' && history.push('/movies');

            console.log(`In tokenCheck: getContent currentUSer: ${currentUser}`);
            console.log(`In tokenCheck: getContent response: ${JSON.stringify(user)}`);
          } else {
            localStorage.removeItem('jwt');
            setIsLoggedIn(false);
          }
        })
        .catch((err) => {
          localStorage.removeItem('jwt');
        })
    } else {
      setIsLoggedIn(false);
    }
  }
  // Получаем все фильмы с API и сохраненные юзером фильмы при авторизации
  useEffect(() => {
    console.log(`Fetching movies...`);

    getMovies();
    getSavedMovies();
  }, [isLoggedIn])

  // Проверяем токен при передвижению по сайту
  useEffect(() => {
    tokenCheck();
  }, [history]);


  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="app">
        <Switch>
          <Route exact path="/">
            <Main
              isLoggedIn={isLoggedIn} />
          </Route>

          <ProtectedRoute
            path="/movies"
            component={Movies}
            movies={movies}
            searchResults={searchResults}
            searchWord={searchWord}
            onLike={handleSetLike}
            onDislike={handleRemoveLike}
            onSearch={handleSearchRequest}
            isLoggedIn={isLoggedIn} />

          <ProtectedRoute
            path="saved-movies"
            component={SavedMovies}
            savedMovies={savedMovies}
            isLoggedIn={isLoggedIn} />

          <ProtectedRoute
            path="/profile"
            component={Profile}
            onSubmit={handleEditProfileSubmit}
            onLogOut={handleLogOut}
            isLoggedIn={isLoggedIn} />

          <Route path="/signin">
            <Login
              handleLogin={handleLogin} />
          </Route>

          <Route path="/signup">
            <Register
              handleRegister={handleRegister} />
          </Route>

          <Route path="*">
            <Page404 />
          </Route>
        </Switch>

        <InfoTooltip
          isOpen={isInfoToolTipOpen}
          onClose={closePopups}
          info={infoToolTipInfo} />
      </div>
    </CurrentUserContext.Provider>
  );
}

