import React from "react";
import Header from '../Header/Header';
import SearchForm from '../SearchForm/SearchForm';
import MoviesCardList from '../MoviesCardList/MoviesCardList';
import Footer from '../Footer/Footer';
import './Movies.css';

function Movies({ movies, searchWord, onLike, onDislike, onSearch, isLoggedIn }) {

  // Запоминаем результаты поиска; 
  // если результатов нет - значит поиск еще не осуществлялся
  // и элемента с фильмами быть не должно

  // console.log(`Movies searchResults: ${searchResults}`);

  const searchResults = localStorage.getItem('searchResults');
  console.log(`000000 Movies searchResults: ${searchResults}`);

  return (
    <div className="movies">
      <Header isLoggedIn={true} />
      <SearchForm
        searchWord={searchWord}
        movies={movies}
        onSearch={onSearch} />
      { searchResults.length > 0 && 
        <MoviesCardList
          searchResults={searchResults}
          onLike={onLike}
          onDislike={onDislike} />
      }

      <Footer />
    </div>
  )
}

export default Movies;
