import React, { useEffect, useState } from "react";
import MoviesCard from '../MoviesCard/MoviesCard';
import './MoviesCardList.css';

function MoviesList({ movieSearchResult, handleSetLike, handleRemoveLike, isLoggedIn }) {

  const [searchResults, setSearchResults] = useState('');

  useEffect(() => {
    if(movieSearchResult.length === 0) {
      setSearchResults([]);
    } else {
      setSearchResults(JSON.parse(localStorage.getItem('searchResults')))
    }
    console.log(`In MoviesList: searchResults: ${movieSearchResult.le}`);
    console.log(`In MoviesList: searchResults: ${JSON.stringify(localStorage.getItem('searchResults'))}`);

    // setSearchResults(movieSearchResult.length > 0 ?
    // movieSearchResult :
    // JSON.parse(localStorage.getItem('searchResults')))
  }, []);

  return (
    <div className="movies-list">
      <div className="movies-list__container">
        {
          searchResults.length > 0 &&
          searchResults.map((movie) => {
            return <MoviesCard
              key={movie.movieId || movie._id}
              movie={movie}
              handleSetLike={handleSetLike}
              handleRemoveLike={handleRemoveLike} />
          })
        }
      </div>

      <button 
        className="movies-list__button-more">
        Ещё
      </button>
    </div>

  )
}

export default MoviesList;
