import React from "react";
import Header from '../Header/Header';
import SearchForm from '../SearchForm/SearchForm';
import MoviesCardList from '../MoviesCardList/MoviesCardList';
import Footer from '../Footer/Footer';
import './Movies.css';

function Movies({ movies, searchResults, searchWord, handleSearchRequest, onLike, onDislike }) {

  // Запоминаем результаты поиска; 
  // если результатов нет - значит поиск еще не осуществлялся
  // и элемента с фильмами быть не должно

  console.log(`Movies searchResults: ${searchResults}`);
  console.log(`Movies searchResults: ${movies}`);

  return (
    <div className="movies">
      <Header isLoggedIn={true} />
      <SearchForm
        searchWord={searchWord} />
      {((searchResults.length > 0 && searchResults !== null) || (movies.length > 0 && movies !== null)) && 
        <MoviesCardList
          searchResults={searchResults}
          handleSearchRequest={handleSearchRequest}
          onLike={onLike}
          onDislike={onDislike} />
      }
      <Footer />
    </div>
  )
}

export default Movies;
