            return <MoviesCard
              key={movie.movieId || movie._id}
              movie={movie}
              onLike={handleSetLike}
              onDislike={handleRemoveLike} />
          })
        }
      </div>

      <button 
        className="movies-list__button-more">
        Ещё
      </button>
    </div>

  )
}

export default MoviesList;
