import React, { useEffect, useState } from "react";
import MoviesCard from '../MoviesCard/MoviesCard';
import './MoviesCardList.css';

function MoviesList({ 
  moviesToRender,
  handleSetLike,
  handleRemoveLike,
  setRender,
  isNoResults,
  isLoggedIn
  }) {

  console.log(`isNoResults? ${isNoResults}`);

  const [displayMovieCount, setDisplayMovieCount] = useState(() => {
    const windowWidth = window.innerWidth;
    if (windowWidth > 1279) {
      return 12;
    } else if (windowWidth >= 768) {
      return 8;
    } else {
      return 4;
    }
  })

  useEffect(() => {
    console.info(localStorage.getItem('searchResults'));
    setRender();
  }, [isLoggedIn])

  return (
    <div className="movies-list">
      { isNoResults && <p className="movies-list__not-found">Ничего не найдено</p> }
      <div className="movies-list__container">
        {
          moviesToRender.length > 0 &&
          moviesToRender.map((movie) => {
            return <MoviesCard
              key={movie.movieId || movie._id}
              movie={movie}
              handleSetLike={handleSetLike}
              handleRemoveLike={handleRemoveLike} />
          })
        }
      </div>

      <button 
        className="movies-list__button-more">
        Ещё
      </button>
    </div>

  )
}

export default MoviesList;
